package Arrays;

public class SpecialSubsequences {

    public static void main(String[] args) {
        System.out.println(solve("..."));
    }

        public static int solve(String A){
            char[] a = A.toCharArray();
            int count =0;
            for(int i = 0; i<a.length; i++){
                if(a[i] == 'A'){
                    for (int j = i+1; j< a.length; j++){
                        if(a[j] == 'G'){
                            count++;
                        }
                    }
                }
            }
            return count;

        }
}
